-- Allow authenticated users to insert their own billing history rows\nDO 13957\nBEGIN\n  IF NOT EXISTS (\n    SELECT 1 FROM pg_policies WHERE schemaname='public' AND tablename='billing_history' AND policyname='billing_history_insert_self'\n  ) THEN\n    EXECUTE 'create policy billing_history_insert_self on public.billing_history for insert with check (auth.uid() = user_id)';\n  END IF;\nEND13957;\n